import { Predicate } from './Predicate';

export interface HasLength { length: number; }

/**
 * @desc Checks if the `value` is of `expectedLength`.
 * Applies to {@link String}s, {@link Array}s and anything that has a `.length` property.
 *
 * @example <caption>Array</caption>
 * import { check, hasLengthOf, TinyType } from 'tiny-types';
 *
 * class Tuple extends TinyType {
 *   constructor(public readonly values: any[]) {
 *      super();
 *      check('Tuple', values, hasLengthOf(2));
 *   }
 * }
 *
 * @example <caption>String</caption>
 * import { check, hasLengthOf, TinyType } from 'tiny-types';
 *
 * class Username extends TinyType {
 *   constructor(public readonly value: string) {
 *      super();
 *      check('Username', value, hasLengthOf(8));
 *   }
 * }
 *
 * @param {number} expectedLength
 * @returns {Predicate}
 */
export function hasLengthOf(expectedLength: number): Predicate<HasLength> {
    const actualLengthOf = (value: HasLength) => (!! value && value.length);

    return Predicate.to(`have a length of ${ expectedLength }`, (value: HasLength) =>
        actualLengthOf(value) === expectedLength,
    );
}

export * from './and';
export * from './hasLengthOf';
export * from './isArray';
export * from './isDefined';
export * from './isEqualTo';
export * from './isGreaterThan';
export * from './isGreaterThanOrEqualTo';
export * from './isInteger';
export * from './isLessThan';
export * from './isLessThanOrEqualTo';
export * from './or';
export * from './Predicate';

import { Predicate } from './Predicate';

/**
 * @desc Checks if the `value` is an {@link Array}.
 *
 * @example
 * import { check, isArray, TinyType, TinyTypeOf } from 'tiny-types';
 *
 * class Name extends TinyTypeOf<string>() {}
 *
 * class Names extends TinyType {
 *   constructor(public readonly values: Name[]) {
 *      super();
 *      check('Names', values, isArray());
 *   }
 * }
 *
 * @returns {Predicate<T[]>}
 */
export function isArray<T>(): Predicate<T[]> {
    return Predicate.to(`be an array`, (value: T[]) => Array.isArray(value));
}

