import { ensure } from './ensure';
import { deprecated } from './objects';

/**
 * @desc This function has been deprecated. Please use {@link ensure} instead
 * @deprecated
 */
export const check = deprecated('Please use `ensure` instead')(ensure);

import { and, Failure, Predicate } from './predicates';

/**
 * @desc The `ensure` function verifies if the value meets the specified {Predicate}s.
 *
 * @example <caption>Basic usage</caption>
 * import { ensure, isDefined } from 'tiny-types'
 *
 * const username = 'jan-molak'
 * ensure('Username', username, isDefined());
 *
 * @example <caption>Ensuring validity of a domain object upon creation</caption>
 * import { TinyType, ensure, isDefined, isInteger, isInRange } from 'tiny-types'
 *
 * class Age extends TinyType {
 *   constructor(public readonly value: number) {
 *     ensure('Age', value, isDefined(), isInteger(), isInRange(0, 125));
 *   }
 * }
 *
 * @param {string} name - the name of the value to check.
 *      This name will be included in the error message should the check fail
 * @param {T} value - the argument to check
 * @param {...Array<Predicate<T>>} predicates - a list of predicates to check the value against
 * @returns {T} - if the original value passes all the predicates, it's returned from the function
 */
export function ensure<T>(name: string, value: T, ...predicates: Array<Predicate<T>>): T {
    const result = and(...predicates).check(value);

    if (result instanceof Failure) {
        throw new Error(`${ name } should ${ result.description }`);    // eslint-disable-line unicorn/prefer-type-error
    }

    return result.value;
}

export * from './check';
export * from './ensure';
export * from './match';
export * from './objects/deprecated';
export * from './pattern-matching/PatternMatcher';
export * from './predicates';
export * from './TinyType';
export { JSONArray, JSONObject, JSONPrimitive, JSONValue, Serialisable } from './types';

import { IdentityMatcher, ObjectMatcher, PatternMatcher, StringMatcher } from './pattern-matching';
import { ConstructorAbstractOrInstance } from './types';

// boolean equality matcher
export function match<Output_Type>(_: boolean): {

    when: (
        pattern: boolean,
        transformation: (_: boolean) => Output_Type,
    ) => PatternMatcher<boolean, boolean, boolean, Output_Type>,
};

// number equality matcher
export function match<Output_Type>(_: number): {

    when: (
        pattern: number,
        transformation: (_: number) => Output_Type,
    ) => PatternMatcher<number, number, number, Output_Type>,
};

// symbol equality matcher
export function match<Output_Type>(_: symbol): {

    when: (
        pattern: symbol,
        transformation: (_: symbol) => Output_Type,
    ) => PatternMatcher<symbol, symbol, symbol, Output_Type>,
};

// string equality and regexp matcher
export function match<Output_Type>(_: string): {
    when: (
        pattern: string | RegExp,
        transformation: (_: string) => Output_Type,
    ) => PatternMatcher<string, string | RegExp, string, Output_Type>,
};

// type matcher
export function match<Input_Type, Output_Type>(_: Input_Type): {

    when: <MT extends Input_Type>(
        pattern: ConstructorAbstractOrInstance<MT>,
        transformation: (v: MT) => Output_Type,
    ) => PatternMatcher<Input_Type, ConstructorAbstractOrInstance<Input_Type>, Input_Type, Output_Type>,
};

/**
 * @experimental
 *
 * @param value
 * @returns {PatternMatcher<any, any, any, any>}
 */
export function match(value: any): PatternMatcher<any, any, any, any> { // eslint-disable-line @typescript-eslint/explicit-module-boundary-types
    switch (true) {
        case typeof value === 'string':
            return new StringMatcher(value as string);
        case typeof value === 'object':
            return new ObjectMatcher(value);
        default:
            return new IdentityMatcher(value);
    }
}

import { Constructor } from '../types';

export type Logger = (_: string) => void;

/**
 * @desc A decorator to mark a class, method or function as deprecated and make it log a warning whenever it is used.
 * Please see the tests for examples of usage.
 *
 * @param {string} message - describes the alternative implementation that should be used instead
 *                  of the deprecated method/function/class
 * @param {Logger} log - a function that handles the printing of the message,
 *                  such as {@link console.warn}
 */
export function deprecated(message = '', log: Logger = console.warn): (target: any, propertyKey?: string, descriptor?: any) => any {    // tslint:disable-line:no-console
    // eslint-disable-next-line unicorn/consistent-function-scoping,no-prototype-builtins
    const hasPrototype = (target: { hasOwnProperty(_: string): boolean }): boolean => target.hasOwnProperty('prototype');

    return (target: any, propertyKey?: string, descriptor?: any): any => {         // tslint:disable-line:ban-types
        if (target && propertyKey && descriptor) {
            return deprecateMethod(message, target, propertyKey, descriptor, log);
        }
        else if (hasPrototype(target)) {
            return deprecateClass(message, target, log);
        }
        else {
            throw new Error(`Only a class, method or function can be marked as deprecated. ${typeof target} given.`);
        }
    };
}

function deprecateClass(message: string, target: Constructor<any>, log: (...args: any[]) => void): Constructor<any> {
    return class extends target {
        constructor(...args: any[]) {
            log(`${target.name} has been deprecated. ${ message }`.trim());

            super(...args);
        }
    };
}

function deprecateMethod<T extends object>(message: string, target: T, propertyKey: string, descriptor: any, log: (...args: any[]) => void) {
    const originalMethod = descriptor.value;

    descriptor.value = function (...args: any[]) {
        log(`${target.constructor.name}#${propertyKey} has been deprecated. ${ message }`.trim());

        return originalMethod.apply(this, args);
    };

    return descriptor;
}

import { significantFieldsOf } from './significantFields';

/**
 * @access private
 */
export function equal(v1: any, v2: any): boolean {  // eslint-disable-line @typescript-eslint/explicit-module-boundary-types
    switch (true) {
        case ! sameType(v1, v2):
            return false;
        case both(arePrimitives, v1, v2):
            return checkIdentityOf(v1, v2);
        case both(areObjects, v1, v2) && sameClass(v1, v2) && both(areDates, v1, v2):
            return checkTimestamps(v1, v2);
        case both(areObjects, v1, v2) && sameClass(v1, v2):
            return checkSignificantFieldsOf(v1, v2);
    }

    return false;
}

const areObjects     = (_: any) => new Object(_) === _;
const areDates       = (_: any) => _ instanceof Date;
const arePrimitives  = (_: any) => ! areObjects(_); // arrays are objects

function both(condition: (_: any) => boolean, v1: any, v2: any): boolean {
    return condition(v1) && condition(v2);
}

const sameType  = (v1: any, v2: any) => typeof v1 === typeof v2;
const sameClass = (v1: any, v2: any) => (v1.constructor && v2 instanceof v1.constructor) || (v2.constructor && v1 instanceof  v2.constructor);
const sameLength = (v1: { length: number }, v2: { length: number }) => v1.length === v2.length;

function checkIdentityOf(v1: any, v2: any) {
    return v1 === v2;
}

function checkTimestamps(v1: Date, v2: Date) {
    return v1.getTime() === v2.getTime();
}

function checkSignificantFieldsOf(o1: object, o2: object) {
    const
        fieldsOfObject1 = significantFieldsOf(o1),
        fieldsOfObject2 = significantFieldsOf(o2);

    if (! sameLength(fieldsOfObject1, fieldsOfObject2)) {
        return false;
    }

    return fieldsOfObject1.reduce((previousFieldsAreEqual: boolean, field: string) => {
        const currentFieldIsEqual = equal(o1[field], o2[field]);
        return previousFieldsAreEqual && currentFieldIsEqual;
    }, true);
}

export * from './deprecated';
export * from './equal';
export * from './isObject';
export * from './isRecord';
export * from './significantFields';
export * from './stringify';
export * from './toJSON';

/**
 * @access private
 */
export function isObject(value: unknown): value is object {
    return value !== null
        && value !== undefined
        && typeof value === 'object'
        && Array.isArray(value) === false
        && Object.prototype.toString.call(value) === '[object Object]';
}

import { isObject } from './isObject';

export function isRecord(value: unknown): value is Record<any, any> {
    if (! isObject(value)) {
        return false;
    }

    // It has modified constructor
    if (value.constructor === undefined) {
        return true;
    }

    // It has modified prototype
    if (! isObject(value.constructor.prototype)) {
        return false;
    }

    // If constructor does not have an Object-specific method
    if (! Object.prototype.hasOwnProperty.call(value.constructor.prototype, 'isPrototypeOf')) {
        return false;
    }

    // Most likely a plain Object
    return true;
}

/**
 * @access private
 *
 * @param {object|Array} o
 * @returns {string[]}
 */
export function significantFieldsOf(o: { [_: string]: any }): string[] {
    return Object.getOwnPropertyNames(o)
        .filter(field => typeof o[field] !== 'function')
        .sort();
}

import { significantFieldsOf } from './significantFields';

/**
 * @access private
 */
export function stringify(v: unknown): string {
    if (Array.isArray(v)) {
        return `${v.constructor.name}(${ v.map(i => stringify(i)).join(', ') })`;
    }

    if (v instanceof Date) {
        return v.toISOString();
    }

    if (isObject(v)) {
        const fields = significantFieldsOf(v)
            .map(field => ({ field, value: stringify(v[field]) }))
            .reduce((acc: string[], current: { field: string, value: string }) => {
                return acc.concat(`${ current.field }=${ current.value }`);
            }, []);

        return `${ v.constructor.name }(${ fields.join(', ') })`;
    }

    return String(v);
}

function isObject(value: any): value is object {
    return new Object(value) === value;
}

/* eslint-disable unicorn/filename-case */
import { JSONObject, JSONValue } from '../types';
import { isRecord } from './isRecord';

/**
 * Serialises the object to a JSON representation.
 *
 * @param value
 */
export function toJSON(value: any): JSONValue | undefined { // eslint-disable-line @typescript-eslint/explicit-module-boundary-types
    switch (true) {
        case value && !! value.toJSON:
            return value.toJSON();
        case value && Array.isArray(value):
            return value.map(v => {
                return v === undefined
                    ? null
                    : toJSON(v) as JSONValue;
            });
        case value && value instanceof Map:
            return mapToJSON(value);
        case value && value instanceof Set:
            return toJSON(Array.from(value));
        case value && isRecord(value):
            return recordToJSON(value);
        case value && value instanceof Error:
            return errorToJSON(value);
        case isSerialisablePrimitive(value):
            return value;
        default:
            return JSON.stringify(value);
    }
}

function mapToJSON(map: Map<any, any>): JSONObject {
    const serialised = Array.from(map, ([key, value]) => [ toJSON(key), toJSON(value) ]);

    return Object.fromEntries(serialised);
}

function recordToJSON(value: Record<any, any>): JSONObject {
    const serialised = Object.entries(value)
        .map(([ k, v ]) => [ toJSON(k), toJSON(v) ]);

    return Object.fromEntries(serialised);
}

function errorToJSON(value: Error): JSONObject {
    return Object.getOwnPropertyNames(value)
        .reduce((serialised, key) => {
            serialised[key] = toJSON(value[key])
            return serialised;
        }, { }) as JSONObject;
}

function isSerialisableNumber(value: unknown): value is number {
    return typeof value === 'number'
        && ! Number.isNaN(value)
        && value !== Number.NEGATIVE_INFINITY
        && value !== Number.POSITIVE_INFINITY;
}

function isSerialisablePrimitive(value: unknown): value is string | boolean | number | null | undefined {
    if (['string', 'boolean'].includes(typeof value)) {
        return true;
    }

    if (value === null || value === undefined) {
        return true;
    }

    return isSerialisableNumber(value);
}

import { PatternMatcher } from './PatternMatcher';
import { MatchesIdentical } from './rules';

/**
 * @access private
 */
export class IdentityMatcher<Input_Type, Output_Type> extends PatternMatcher<Input_Type, Input_Type, Input_Type, Output_Type> {

    when(pattern: Input_Type, transformation: (v: Input_Type) => Output_Type): PatternMatcher<Input_Type, Input_Type, Input_Type, Output_Type> {
        return new IdentityMatcher(
            this.value,
            this.rules.concat(new MatchesIdentical(pattern, transformation)),
        );
    }
}

import { TinyType } from '../TinyType';
import { ConstructorAbstractOrInstance } from '../types';
import { PatternMatcher } from './PatternMatcher';
import { MatcherRule, MatchesEqualTinyType, MatchesIdentical, MatchesObjectsWithCommonPrototype } from './rules';

/**
 * @access private
 */
export class ObjectMatcher<Input_Type, Output_Type> extends PatternMatcher<Input_Type, TinyType | ConstructorAbstractOrInstance<Input_Type>, TinyType | Input_Type, Output_Type> {

    when<MT extends Input_Type>(pattern: ConstructorAbstractOrInstance<MT>, transformation: (v: MT) => Output_Type): ObjectMatcher<Input_Type, Output_Type>;
    when(pattern: TinyType, transformation: (v: TinyType) => Output_Type): ObjectMatcher<Input_Type, Output_Type>;
    when(pattern: Input_Type, transformation: (v: Input_Type) => Output_Type): ObjectMatcher<Input_Type, Output_Type>;
    when(pattern: any, transformation: (v: any) => Output_Type) {   // eslint-disable-line @typescript-eslint/explicit-module-boundary-types
        return new ObjectMatcher(
            this.value,
            this.rules.concat(this.rule(pattern, transformation)),
        );
    }

    private rule(pattern: any, transformation: (v: any) => Output_Type): MatcherRule<any, Output_Type> {
        switch (true) {
            case pattern instanceof TinyType:
                return new MatchesEqualTinyType<Output_Type>(pattern as TinyType, transformation);
            case typeof pattern === 'function':
                return new MatchesObjectsWithCommonPrototype<any, Output_Type>(pattern, transformation);
            default:
                return new MatchesIdentical<Input_Type, Output_Type>(pattern, transformation);
        }
    }
}

import { List } from '../types';
import { MatcherRule, MatchesAnything } from './rules';

/**
 * @access private
 */
export abstract class PatternMatcher<Input_Type, Pattern_Type, Matching_Type, Output_Type> {
    constructor(
        protected readonly value: Input_Type,
        protected readonly rules: List<MatcherRule<Input_Type, Output_Type>> = []) {
    }

    abstract when(pattern: Pattern_Type, transformation: (v: Matching_Type) => Output_Type): PatternMatcher<Input_Type, Pattern_Type, Matching_Type, Output_Type>;

    else(transformation: (v: Input_Type) => Output_Type): Output_Type {
        const matching_rule = this.rules.find(m => m.matches(this.value));

        return (matching_rule || new MatchesAnything(transformation)).execute(this.value);
    }
}

import { PatternMatcher } from './PatternMatcher';
import { MatchesIdentical, MatchesRegExp } from './rules';

/**
 * @access private
 */
export class StringMatcher<Output_Type> extends PatternMatcher<string, string | RegExp, string, Output_Type> {

    when(pattern: string | RegExp, transformation: (v: string) => Output_Type): PatternMatcher<string, string | RegExp, string, Output_Type> {
        const rule = pattern instanceof RegExp
            ? new MatchesRegExp(pattern, transformation)
            : new MatchesIdentical(pattern, transformation);

        return new StringMatcher<Output_Type>(
            this.value,
            this.rules.concat(rule),
        );
    }
}

export * from './IdentityMatcher';
export * from './ObjectMatcher';
export * from './PatternMatcher';
export * from './StringMatcher';

/**
 * @access private
 */
export abstract class MatcherRule<Input_Type, Output_Type>{
    constructor(
        private readonly transformation: (v: Input_Type) => Output_Type,
    ) {
    }

    abstract matches(value: Input_Type): boolean;

    execute(value: Input_Type): Output_Type {
        return this.transformation(value);
    }
}

import { MatcherRule } from './MatcherRule';

/**
 * @access private
 */
export class MatchesAnything<Input_Type, Output_Type> extends MatcherRule<Input_Type, Output_Type> {
    constructor(transformation: (v: Input_Type) => Output_Type) {
        super(transformation);
    }

    matches(_: Input_Type): boolean {
        return true;
    }
}

import { TinyType } from '../../TinyType';
import { MatcherRule } from './MatcherRule';

/**
 * @access private
 */
export class MatchesEqualTinyType<Output_Type> extends MatcherRule<TinyType, Output_Type> {
    constructor(private readonly pattern: TinyType, transformation: (v: TinyType) => Output_Type) {
        super(transformation);
    }

    matches(value: TinyType): boolean {
        return this.pattern.equals(value);
    }
}

import { MatcherRule } from './MatcherRule';

/**
 * @access private
 */
export class MatchesIdentical<Input_Type, Output_Type> extends MatcherRule<Input_Type, Output_Type> {
    constructor(private readonly pattern: Input_Type, transformation: (v: Input_Type) => Output_Type) {
        super(transformation);
    }

    matches(value: Input_Type): boolean {
        return value === this.pattern;
    }
}

import { ConstructorOrAbstract } from '../../types';
import { MatcherRule } from './MatcherRule';

/**
 * @access private
 */
export class MatchesObjectsWithCommonPrototype<Input_Type, Output_Type> extends MatcherRule<Input_Type, Output_Type> {
    constructor(
        private readonly pattern: ConstructorOrAbstract<Input_Type>,
        transformation: (v: Input_Type) => Output_Type,
    ) {
        super(transformation);
    }

    matches(value: Input_Type): boolean {
        return value instanceof this.pattern;
    }
}

import { MatcherRule } from './MatcherRule';

/**
 * @access private
 */
export class MatchesRegExp<Output_Type> extends MatcherRule<string, Output_Type> {
    constructor(private readonly pattern: RegExp, transformation: (v: string) => Output_Type) {
        super(transformation);
    }

    matches(value: string): boolean {
        return this.pattern.test(value);
    }
}

export * from './MatcherRule';
export * from './MatchesAnything';
export * from './MatchesEqualTinyType';
export * from './MatchesIdentical';
export * from './MatchesObjectsWithCommonPrototype';
export * from './MatchesRegExp';

/**
 * @access public
 */
export abstract class Result<T> {
    constructor(public readonly value: T) {}
}

/**
 * @access public
 */
export class Success<T> extends Result<T> {}

/**
 * @access public
 */
export class Failure<T> extends Result<T> {
    constructor(value: T, public readonly description: string) {
        super(value);
    }
}

/**
 * @access public
 */
export type Condition<T> = (value: T) => boolean;

/**
 * @desc Describes a {@link Condition} that the `value` should meet.
 *
 * To define a custom predicate to be used with the {@link check} function
 * you can either extend the {@link Predicate}, or use the {@link Predicate.to} factory method.
 *
 * @example <caption>Assuming we'd like to create an isDefined() predicate:</caption>
 * ensure(`some value`, value, isDefined());
 *
 * @example <caption>We can either use the Predicate.to factory method:</caption>
 *
 * import { Predicate } from 'tiny-types';
 *
 * function isDefined<T>(): Predicate<T> {
 *     return Predicate.to(`be defined`, (value: T) =>
 *         ! (value === null || value === undefined),
 *     );
 * }
 *
 * @example <caption>or extend the Predicate itself</caption>
 *
 * import { Predicate, Result, Success, Failure } from 'tiny-types';
 *
 * function isDefined<T>() {
 *   return new IsDefined<T>();
 * }
 *
 * class IsDefined<T> extends Predicate<T> {
 *     check(value: T): Result<T> {
 *       return ! (value === null || value === undefined)
 *         ? new Success(value)
 *         : new Failure(value, `be defined`);
 *     }
 * }
 *
 * @access public
 */
export abstract class Predicate<T> {

    /**
     * @desc A factory method instantiating a single-condition predicate.
     * You can use it instead of extending the {Predicate} to save some keystrokes.
     *
     * @example
     * Predicate.to(`be defined`, (value: T) => ! (value === null || value === undefined));
     *
     * @param {string} description     - The description of the condition is used by {@link check} to generate the error
     *                                   message. The description should be similar to`be defined`,
     *                                   `be less than some value` for the error message to make sense.
     * @param {Condition<V>} condition - a function that takes a value of type `V` and returns a boolean
     *                                   indicating whether or not the condition is met. For example:
     *                                   `(value: V) => !! value`
     * @returns {Predicate<V>}
     *
     * @static
     */
    static to<V>(description: string, condition: Condition<V>): Predicate<V> {
        return new SingleConditionPredicate<V>(description, condition);
    }

    abstract check(value: T): Result<T>;
}

/**
 * @access private
 */
class SingleConditionPredicate<T> extends Predicate<T> {
    constructor(
        private readonly description: string,
        private readonly isMetBy: Condition<T>,
    ) {
        super();
    }

    /** @override */
    check(value: T): Result<T> {
        return this.isMetBy(value)
            ? new Success(value)
            : new Failure(value, this.description);
    }
}

import { isArray } from './isArray';
import { isDefined } from './isDefined';
import { isGreaterThan } from './isGreaterThan';
import { Failure, Predicate, Result, Success } from './Predicate';

/**
 * @desc Ensures that the `value` meets all the provided {@link Predicate}s.
 *
 * @example
 * import { and, ensure, isDefined, isGreaterThan, isInteger, TinyType } from 'tiny-types';
 *
 * class AgeInYears extends TinyType {
 *     constructor(public readonly value: number) {
 *         ensure('AgeInYears', value, and(isDefined(), isInteger(), isGreaterThan(18));
 *     }
 * }
 *
 * @param {...Array<Predicate<T>>} predicates
 * @returns {Predicate<T>}
 */
export function and<T>(...predicates: Array<Predicate<T>>): Predicate<T> {
    return new And<T>(predicates);
}

/** @access private */
class And<T> extends Predicate<T> {

    constructor(private readonly predicates: Array<Predicate<T>>) {
        super();

        const results = [
            _ => isDefined().check(_),
            _ => isArray().check(_),
            _ => isGreaterThan(0).check(_.length),
        ];

        if (results.some(check => check(this.predicates) instanceof Failure)) {
            throw new Error(`Looks like you haven't specified any predicates to check the value against?`);
        }
    }

    /** @override */
    check(value: T): Result<T> {
        for (const predicate of this.predicates) {
            const result = predicate.check(value);
            if (result instanceof Failure) {
                return result;
            }
        }

        return new Success(value);
    }
}

