import 'mocha';

import { given } from 'mocha-testdata';

import { match, TinyType, TinyTypeOf } from '../src';
import { IdentityMatcher, ObjectMatcher, StringMatcher } from '../src/pattern-matching';
import { expect } from './expect';

/** @test {match} */
describe(`::match`, () => {

    /** @test {match} */
    describe('default rules', () => {
        it(`uses the default rule when no identical match is found for a string value`, () => {
            const result = match('four')
                .when('five', _ => `high five`)
                .when('six', _ => `got your six`)
                .else(n => `got ${n}`);

            expect(result).to.equal('got four');
        });

        it(`uses the default rule when no identical match is found for a numerical value`, () => {
            const result = match(4)
                .when(5, _ => `high five`)
                .when(6, _ => `got your six`)
                .else(n => `got ${n}`);

            expect(result).to.equal('got 4');
        });
    });

    describe('when selecting a matcher', () => {
        abstract class DomainEvent {
            constructor(public readonly timestamp: Date = new Date()) {
            }
        }

        class ConcreteEvent extends DomainEvent {
        }

        class EmaiAddress extends TinyType {
            constructor(public readonly value: string) {
                super();
            }
        }

        given([
            { input: 5,                                     pattern: 1,                                     expected_matcher: IdentityMatcher },
            { input: Symbol('some'),                        pattern: Symbol('other'),                       expected_matcher: IdentityMatcher },
            { input: 'hello',                               pattern: 'hello',                               expected_matcher: StringMatcher },
            { input: 'hello',                               pattern: /^[Hh]ello/,                           expected_matcher: StringMatcher },
            { input: new EmaiAddress('user@domain.org'),    pattern: new EmaiAddress('user@domain.org'),    expected_matcher: ObjectMatcher },
            { input: new ConcreteEvent(),                   pattern: ConcreteEvent,                         expected_matcher: ObjectMatcher },
            { input: new ConcreteEvent(),                   pattern: DomainEvent,                           expected_matcher: ObjectMatcher },
        ]).
        it(`uses a matcher appropriate to the input`, ({input, pattern, expected_matcher}) => {
            expect(match(input).when(pattern, _ => _)).to.be.instanceOf(expected_matcher);
        });
    });

    /**
     * @test {match}
     * @test {TinyType}
     * @test {TinyTypeOf}
     */
    describe('when working with TinyTypes', () => {

        class AccountId extends TinyTypeOf<number>() {}
        abstract class Command extends TinyTypeOf<AccountId>() {}
        class OpenAccount extends Command {}
        class CloseAccount extends Command {}
        class SuspendAccount extends Command {}

        given([
            { command: new OpenAccount(new AccountId(42)),      expected: 'Open AccountId(value=42)'                            },
            { command: new CloseAccount(new AccountId(42)),     expected: 'Close AccountId(value=42)'                           },
            { command: new SuspendAccount(new AccountId(42)),   expected: 'Command: SuspendAccount(value=AccountId(value=42))'  },
            { command: null,                                    expected: 'Unrecognised input: null'                            },
        ]).
        it('matches a TinyType by type', ({ command, expected }) => {
            const result = match(command)
                .when(OpenAccount,  ({ value }) => `Open ${ value }`)
                .when(CloseAccount, ({ value }) => `Close ${ value }`)
                .when(Command,      _ => `Command: ${ _ }`)
                .else(_ => `Unrecognised input: ${_}`);

            expect(result).to.equal(expected);
        });

        given([
            { command: new OpenAccount(new AccountId(42)),      expected: 'Open AccountId(value=42)'                            },
            { command: new CloseAccount(new AccountId(42)),     expected: 'Close AccountId(value=42)'                           },
            { command: new SuspendAccount(new AccountId(42)),   expected: 'Command: SuspendAccount(value=AccountId(value=42))'  },
            { command: null,                                    expected: 'Unrecognised input: null'                            },
        ]).
        it('matches a TinyType by value', ({ command, expected }) => {
            const result = match(command)
                .when(new OpenAccount(new AccountId(42)),   ({ value }) => `Open ${ value }`)
                .when(new CloseAccount(new AccountId(42)),  ({ value }) => `Close ${ value }`)
                .when(new SuspendAccount(new AccountId(42)), _ => `Command: ${ _ }`)
                .else(_ => `Unrecognised input: ${_}`);

            expect(result).to.equal(expected);
        });
    });
});

import 'mocha';

import * as sinon from 'sinon';

import { deprecated } from '../../src/objects';
import { expect } from '../expect';

/** @test {deprecated} */
describe('deprecated', () => {

    describe('when used to annotate a class', () => {
        it('logs a warning when the class is constructed', () => {
            const consoleWarn = sinon.spy();

            @deprecated(undefined, consoleWarn)
            class Foo {
            }

            const foo_ = new Foo();

            expect(consoleWarn)
                .to.have.been.calledWith('Foo has been deprecated.');
        });

        it('can provide additional suggestion on what other class should be used instead', () => {
            const consoleWarn = sinon.spy();

            @deprecated('Please use Bar instead.', consoleWarn)
            class Foo {
            }

            const foo_ = new Foo();

            expect(consoleWarn)
                .to.have.been.calledWith('Foo has been deprecated. Please use Bar instead.');
        });

        it('maintains the type and behaviour of the annotated class', () => {
            const consoleWarn = sinon.spy();

            @deprecated('Please use Client instead.', consoleWarn)
            class Person {
                constructor(public readonly name: string) {
                }
            }

            const p = new Person('Alice');

            expect(consoleWarn)
                .to.have.been.calledWith('Person has been deprecated. Please use Client instead.');

            expect(p).to.be.instanceOf(Person);
        });
    });

    describe('when used to annotate a method', () => {

        it('logs a warning when the method is used', () => {
            const consoleWarn = sinon.spy();

            class Person {
                @deprecated('', consoleWarn)
                greet()   { return null; }
                welcome() { return null; }
            }

            const p = new Person();
            p.greet();

            expect(consoleWarn)
                .to.have.been.calledWith('Person#greet has been deprecated.');
        });

        it('can provide additional suggestion on what other method should be used instead', () => {
            const consoleWarn = sinon.spy();

            class Person {

                @deprecated('Please use Person#welcome instead.', consoleWarn)
                greet()   { return null; }
                welcome() { return null; }
            }

            const p = new Person();
            p.greet();

            expect(consoleWarn)
                .to.have.been.calledWith('Person#greet has been deprecated. Please use Person#welcome instead.');
        });

        it('maintains the behaviour of the annotated method', () => {
            const consoleWarn = sinon.spy();

            class Person {
                constructor(public readonly name: string) {
                }

                @deprecated('Please use Person#welcome instead.', consoleWarn)
                greet(greeting: string) {
                    return `${ greeting }, my name is ${this.name}`;
                }
            }

            const p = new Person('Alice');

            expect(p.greet('Hi')).to.equal('Hi, my name is Alice');

            expect(consoleWarn)
                .to.have.been.calledWith('Person#greet has been deprecated. Please use Person#welcome instead.');
        });
    });

    describe('when used incorrectly', () => {

        it('complains', () => {
            expect(() => deprecated('something that does not make sense')(42))
                .to.throw(`Only a class, method or function can be marked as deprecated. number given.`);
        });
    })
});

import 'mocha';

import { given } from 'mocha-testdata';

import { TinyType, TinyTypeOf } from '../../src';
import { equal } from '../../src/objects';
import { expect } from '../expect';

/** @test {equal} */
describe('equal', () => {
    describe('when used with primitives', () => {

        given<any>(undefined, null, false, 'string', 42).
        it('is reflexive', (primitive: any) => {
            expect(equal(primitive, primitive)).to.be.true;                  // tslint:disable-line:no-unused-expression
        });

        given<{ v1: any, v2: any }>(
            { v1: false,    v2: false             },
            { v1: false,    v2: Boolean(false)    },
            { v1: 'string', v2: 'string'          },
            { v1: 'string', v2: String('string')  },
            { v1: 42,       v2: 42                },
            { v1: 42,       v2: Number(42)        },
            { v1: 42,       v2: 42              },
            { v1: 42,       v2: Number(42)      },
        ).
        it('is symmetric', ({ v1, v2 }) => {
            expect(equal(v1, v2)).to.be.true;                                // tslint:disable-line:no-unused-expression
            expect(equal(v1, v2)).to.equal(equal(v2, v1));
        });

        given<{ v1: any, v2: any, v3: any }>(
            { v1: false,    v2: false,    v3: false     },
            { v1: 'string', v2: 'string', v3: 'string'  },
            { v1: 42,       v2: 42,       v3: 42        },
        ).
        it('is transitive', ({ v1, v2, v3 }) => {
            expect(equal(v1, v2)).to.be.true;                               // tslint:disable-line:no-unused-expression
            expect(equal(v2, v3)).to.be.true;                               // tslint:disable-line:no-unused-expression
            expect(equal(v3, v1)).to.be.true;                               // tslint:disable-line:no-unused-expression
        });

        given<{ v1: any, v2: any }>(
            { v1: false,    v2: true       },
            { v1: 'apple',  v2: 'orange'   },
            { v1: 42,       v2: 24         },
            { v1: false,    v2: 'elephant' },
            { v1: null,     v2: undefined  },
        ).
        it('returns false when subjects are not equal', ({ v1, v2 }) => {
            expect(equal(v1, v2)).to.be.false;                               // tslint:disable-line:no-unused-expression
        });
    });

    /** @test {TinyType#equals} */
    describe('when used with TinyTypes', () => {

        class Name extends TinyTypeOf<string>() {}
        class Age  extends TinyTypeOf<number>() {}

        class Person extends TinyType {
            constructor(public readonly name: Name, public readonly role: Age) {
                super();
            }
        }

        // Please note that there's not much point in wrapping an array into a tiny type, as it doesn't provide
        // methods you'd expect from a list, such as map, reduce, forEach, etc.
        // What People is here to demonstrate is that `equals` works equally well (pun intended)
        // with tiny types with a member field of type Array.
        class People extends TinyTypeOf<Person[]>() {}

        const
            Alice   = new Name('Alice'),
            Bob     = new Name('Bob'),
            MsAlice = new Person(Alice, new Age(28)),
            MrBob   = new Person(Alice, new Age(38)),
            Team    = new People([MsAlice, MrBob]),
            Team2   = new People([MsAlice]);

        given<TinyType>(Alice, MsAlice, Team).
        it('is reflexive', (value: TinyType) => {
            expect(equal(value, value)).to.be.true;                          // tslint:disable-line:no-unused-expression
        });

        given<{ v1: TinyType, v2: TinyType }>(
            { v1: new Name('Alice'),    v2: new Name('Alice')   },
            { v1: new Age(28),          v2: new Age(28)         },
            { v1: Team,                 v2: Team                },
            { v1: new Person(Alice, new Age(28)), v2: new Person(Alice, new Age(28))  },
        ).
        it('is symmetric', ({ v1, v2 }) => {
            expect(equal(v1, v2)).to.be.true;                                // tslint:disable-line:no-unused-expression
            expect(equal(v1, v2)).to.equal(equal(v2, v1));
        });

        given<{ v1: TinyType, v2: TinyType, v3: TinyType }>(
            { v1: new Name('Alice'),    v2: new Name('Alice'),  v3: new Name('Alice') },
            { v1: new Age(28),          v2: new Age(28),        v3: new Age(28)       },
            { v1: new Person(Alice, new Age(28)), v2: new Person(Alice, new Age(28)), v3: new Person(Alice, new Age(28)) },
        ).
        it('is transitive', ({ v1, v2, v3 }) => {
            expect(equal(v1, v2)).to.be.true;                                // tslint:disable-line:no-unused-expression
            expect(equal(v2, v3)).to.be.true;                                // tslint:disable-line:no-unused-expression
            expect(equal(v3, v1)).to.be.true;                                // tslint:disable-line:no-unused-expression
        });

        given<{ v1: any, v2: any }>(
            { v1: Alice,    v2: null    },
            { v1: Alice,    v2: Bob     },
            { v1: Bob,      v2: 'cat'   },
            { v1: Alice,    v2: MsAlice },
            { v1: MsAlice,  v2: MrBob   },
            { v1: MrBob,    v2: Team    },
            { v1: Team,     v2: Team2   },
        ).
        it('returns false when subjects are not equal', ({ v1, v2 }) => {
            expect(equal(v1, v2)).to.be.false;                               // tslint:disable-line:no-unused-expression
        });

        it('compares public and private member fields', () => {
            class PrivatePerson extends TinyType {
                constructor(private readonly name: Name, private readonly age: Age) {
                    super();
                }
            }

            const
                PrivateAlice = new PrivatePerson(new Name('Alice'), new Age(28)),
                PrivateRyan  = new PrivatePerson(new Name('Ryan'), new Age(28));

            expect(PrivateAlice.equals(PrivateAlice)).to.be.true;            // tslint:disable-line:no-unused-expression
            expect(PrivateAlice.equals(PrivateRyan)).to.be.false;            // tslint:disable-line:no-unused-expression
        });
    });

    /** @test {TinyType#equals} */
    describe('when used with TinyTypes with optional fields', () => {

        class FirstName extends TinyTypeOf<string>() {}
        class LastName extends TinyTypeOf<string>() {}

        class Magician extends TinyType {
            constructor(
                public readonly firstName: FirstName,
                public readonly lastName?: LastName,
            ) {
                super();
            }
        }

        it('returns true when the non-optional fields are equal', () => {
            const
                t1 = new Magician(new FirstName('Teller')),
                t2 = new Magician(new FirstName('Teller'));

            expect(equal(t1, t2)).to.equal(true);
        });
    });

    /** @test {TinyType#equals} */
    describe('when used with Dates', () => {
        const
            dateInstance1 = new Date('2018-05-01T12:00:00.000Z'),
            dateInstance2 = new Date('2018-05-01T12:00:00.000Z'),
            dateInstance3 = new Date('2018-05-01T12:00:00.000Z'),
            differentDate = new Date('2042-05-01T12:15:30.000Z');

        it('is reflexive', () => {
            expect(equal(dateInstance1, dateInstance1)).to.be.true;          // tslint:disable-line:no-unused-expression
        });

        it('is symmetric', () => {
            expect(equal(dateInstance1, dateInstance2)).to.be.true;          // tslint:disable-line:no-unused-expression
            expect(equal(dateInstance2, dateInstance1)).to.equal(equal(dateInstance1, dateInstance2));
        });

        it('is transitive', () => {
            expect(equal(dateInstance1, dateInstance2)).to.be.true;          // tslint:disable-line:no-unused-expression
            expect(equal(dateInstance2, dateInstance3)).to.be.true;          // tslint:disable-line:no-unused-expression
            expect(equal(dateInstance3, dateInstance1)).to.be.true;          // tslint:disable-line:no-unused-expression
        });

        it('returns false when subjects are not equal', () => {
            expect(equal(dateInstance1, differentDate)).to.be.false;         // tslint:disable-line:no-unused-expression
        });
    });

    /** @test {TinyType#equals} */
    describe('when used with Arrays', () => {
        class Name extends TinyTypeOf<string>() {}

        it('returns false when arrays are of different length', () => {
            expect(equal(
                [ new Name('Alice'), new Name('Bob') ],
                [ new Name('Alice'), new Name('Bob'), new Name('Cyril') ],
            )).to.equal(false);
        });

        it('returns false when arrays contain different items', () => {
            expect(equal(
                [ new Name('Alice'), new Name('Bob'), new Name('Cyril')],
                [ new Name('Alice'), new Name('Bob'), new Name('Cynthia') ],
            )).to.equal(false);
        });

        it('returns true when both arrays contain equal items', () => {
            expect(equal(
                [ new Name('Alice'), new Name('Bob'), new Name('Cynthia') ],
                [ new Name('Alice'), new Name('Bob'), new Name('Cynthia') ],
            )).to.equal(true);
        });
    });
});

import 'mocha';

import { significantFieldsOf } from '../../src/objects';
import { expect } from '../expect';

/** @test {significantFields} */
describe('significantFields', () => {
    it('returns the names of private and public member fields of an instantiated object', () => {
