import 'mocha';

import { given } from 'mocha-testdata';

import { ensure, isDefined, isEqualTo, isGreaterThan, isInteger, isLessThan, or, TinyType } from '../../src';
import { expect } from '../expect';

describe('predicates', () => {

    /** @test {or} */
    describe('::or', () => {

        class Percentage extends TinyType {
            constructor(public readonly value: number) {
                super();
                ensure('Percentage', value,
                    isDefined(),
                    isInteger(),
                    or(isEqualTo(0), isGreaterThan(0)),
                    or(isLessThan(100), isEqualTo(100)),
                );
            }
        }

        given(0, 1, 99, 100).
        it('ensures that at least one of the `or` predicates is met', (value: number) => {
            expect(() => new Percentage(value)).to.not.throw();
        });

        given(
            [-1,  'Percentage should either be equal to 0 or be greater than 0'],
            [101, 'Percentage should either be less than 100 or be equal to 100'],
        ).
        it('complains if at least one of the `or` predicates is not met', (value: number, errorMessage: string) => {
            expect(() => new Percentage(value))
                .to.throw(errorMessage);
        });

        it('complains if there are no predicates specified', () => {
            expect(() => or()).to.throw(`Looks like you haven't specified any predicates to check the value against?`);
        });

        it('concatenates the error messages in a human-friendly way', () => {
            expect(() => ensure('Project name', 'node.js',
                or(isEqualTo('Serenity/JS'), isEqualTo('TinyTypes'), isEqualTo('Build Monitor')),
            )).to.throw(
                'Project name should either be equal to Serenity/JS, be equal to TinyTypes or be equal to Build Monitor',
            );
        });
    });
});

import 'mocha';

import { given } from 'mocha-testdata';

import { ensure, isDefined, isGreaterThanOrEqualTo, isString, property, TinyType } from '../../src';
import { expect } from '../expect';

describe('predicates', () => {

    /** @test {property} */
    describe('::property', () => {

        /** @test {property} */
        describe('shifts the focus from the value itself to its property', () => {

            class Name extends TinyType {
                constructor(public readonly value: string) {
                    super();
                    ensure('Name', value,
                        isDefined(),
                        property('length', isDefined(), isGreaterThanOrEqualTo(3)),
                        isString(),
                    );
                }
            }

            /** @test {property} */
            it('ensures that the property meets the predicates', () => {
                expect(() => new Name('Jan')).to.not.throw();
            });

            given(
                undefined,
                null,
            ).
            it('complains when the value is undefined', (value: any) => {
                expect(() => new Name({ length: value } as any)).
                    to.throw(`Name should have a property "length" that is defined`);
            });

            given<any, string>(
                [undefined,             'Name should be defined'],
                [{ length: undefined }, 'Name should have a property "length" that is defined'],
                ['JM',                  'Name should have a property "length" that is either equal to 3 or is greater than 3'],
                [['J', 'a', 'n'],       'Name should be a string'],
            ).
            it('can be composed with other predicates', (value: any, expectedError: string) => {
                expect(() => new Name(value)).
                    to.throw(expectedError);
            });
        });
    });
});

import 'mocha';

import { given } from 'mocha-testdata';

import { ensure, startsWith, TinyType } from '../../src';
import { expect } from '../expect';

describe('predicates', () => {

    /** @test {startsWith} */
    describe('::startsWith', () => {

        class Username extends TinyType {
            constructor(public readonly value: string) {
                super();

                ensure('Username', value, startsWith('usr'));
            }
        }

        it('ensures that the argument starts with a given prefix', () => {
            expect(() => new Username('usr123')).to.not.throw();
        });

        given([
            undefined,
            null,
            {},
            'string',
            5,
        ]).
        it('complains if the value does not start with the given prefix', (value: any) => {
            expect(() => new Username(value))
                .to.throw(`Username should start with 'usr'`);
        });
    });
});

import 'mocha';

import { given } from 'mocha-testdata';

import { JSONObject, TinyType, TinyTypeOf } from '../src';
import { expect } from './expect';

describe('Serialisation', () => {

    describe('of single-value TinyTypes', () => {

        class Amount extends TinyTypeOf<number>() {}
        class Name extends TinyTypeOf<string>() {}
        class Vote extends TinyTypeOf<boolean>() {}
        class Maybe extends TinyType {
            constructor(public readonly value: any) {
                super();
            }
        }

        given([
            { obj: new Amount(0),        expectedType: 'number'    },
            { obj: new Amount(10/3),     expectedType: 'number'    },
            { obj: new Name('Jan'),      expectedType: 'string'    },
            { obj: new Vote(true),       expectedType: 'boolean'   },
            { obj: new Vote(false),      expectedType: 'boolean'   },
            { obj: new Maybe(undefined), expectedType: 'undefined' },
        ]).
        it('preserves the type of the wrapped value', ({ obj, expectedType: expectedType }) => {
            expect(obj.toJSON()).to.be.an(expectedType);
        });
    });

    describe('of TinyTypes wrapping several primitive values', () => {

        class Person extends TinyType {
            public role = 'dev';

            static fromJSON(o: JSONObject): Person {
                return new Person(o.firstName as string, o.lastName as string, o.age as number);
            }

            constructor(public readonly firstName: string, public readonly lastName: string, public readonly age: number) {
                super();
            }

            speak() {
                return `Hi, I'm ${this.firstName} ${this.lastName}`;
            }
        }

        it('uses only the significant fields and retains their type', () => {
            const p = new Person('John', 'Smith', 42);
            const serialised = p.toJSON() as JSONObject;

            expect(Object.keys(serialised)).to.include.ordered.members(['age', 'firstName', 'lastName', 'role']);
            expect(Object.keys(serialised)).to.not.include.members(['speak', 'toJSON', 'toString']);

            expect(serialised.age).to.be.a('number');
            expect(serialised.firstName).to.be.a('string');
            expect(serialised.lastName).to.be.a('string');
            expect(serialised.role).to.be.a('string');
        });
    });

    describe('of nested Tiny Types', () => {
        class FirstName extends TinyTypeOf<string>() {
            static fromJSON = (v: string) => new FirstName(v);
        }
        class LastName  extends TinyTypeOf<string>() {
            static fromJSON = (v: string) => new LastName(v);
        }
        class Age extends TinyTypeOf<number>() {
            static fromJSON = (v: number) => new Age(v);
        }

        class AnotherPerson extends TinyType {
            public role = 'dev';

            static fromJSON(o: JSONObject): AnotherPerson {
                return new AnotherPerson(
                    FirstName.fromJSON(o.firstName as string),
                    LastName.fromJSON(o.lastName as string),
                    Age.fromJSON(o.age as number),
                );
            }

            constructor(public readonly firstName: FirstName,
                public readonly lastName: LastName,
                public readonly age: Age,
            ) {
                super();
            }

            speak() {
                return `Hi, I'm ${this.firstName} ${this.lastName}`;
            }
        }

        it('uses only the significant fields and retains the type of their respective values', () => {
            const p = new AnotherPerson(new FirstName('John'), new LastName('Smith'), new Age(42));
            const serialised = p.toJSON() as JSONObject;

            expect(Object.keys(serialised)).to.include.ordered.members(['age', 'firstName', 'lastName', 'role']);
            expect(Object.keys(serialised)).to.not.include.members(['speak', 'toJSON', 'toString']);

            expect(serialised.age).to.be.a('number');
            expect(serialised.firstName).to.be.a('string');
            expect(serialised.lastName).to.be.a('string');
            expect(serialised.role).to.be.a('string');
        });
    });

    it('works both ways', () => {
        class EmployeeId extends TinyTypeOf<number>() {
            static fromJSON = (id: number) => new EmployeeId(id);
        }

        class DepartmentId extends TinyTypeOf<string>() {
            static fromJSON = (id: string) => new DepartmentId(id);
        }

        class Allocation extends TinyType {
            static fromJSON = (o: JSONObject) => new Allocation(
                EmployeeId.fromJSON(o.employeeId as number),
                DepartmentId.fromJSON(o.departmentId as string),
            )

            constructor(public readonly employeeId: EmployeeId, public readonly departmentId: DepartmentId) {
                super();
            }
        }

        const allocation = new Allocation(new EmployeeId(1), new DepartmentId('engineering'));

        const deserialised = Allocation.fromJSON({ departmentId: 'engineering', employeeId: 1 });

        expect(deserialised.equals(allocation)).to.be.true;                 // tslint:disable-line:no-unused-expression
    });
});
